    public static boolean checkPhoneNumber(String value) {
        Pattern pattern = Pattern.compile("[0]{1}[1,9]{1}[0-9]{8,9}");
        Matcher matcher = pattern.matcher(value);
        if (!matcher.find()) {
            return false;
        } else {
            return true;
        }
    }
